import React, { useState } from 'react'; 
import * as styles from './App.module.scss';
import RegionBox from './components/regionBox/RegionBox'; 
import AdminPanel from './components/adminPanel/AdminPanel'; 
import Footer from './components/footer/Footer'; 


const body = 
[ 
    {
        "id":"123", 
        "continent":"Europe", 
        "country":"Netherlands",
        "city":"Amsterdam", 
        "utcoffset":"60",
        "openHoursOne":"11:00-14:00",
        "openingHoursTwo":"15:00-17:00" 

    },
    {
        "id":"456", 
        "continent":"Asia", 
        "country":"China",
        "city":"Shanghai", 
        "utcoffset":"480",
        "openHoursOne":"11:00-14:00",
        "openingHoursTwo":"15:00-17:00" 

    }, 
    {
        "id":"789", 
        "continent":"South America", 
        "country":"Brazil",
        "city":"San Paolo", 
        "utcoffset":"180",
        "openHoursOne":"11:00-14:00",
        "openingHoursTwo":"15:00-17:00" 

    }, 

    {
      "id":"1122", 
      "continent":"Europe", 
      "country":"Ukraine",
      "city":"Kyiv", 
      "utcoffset":"60",
      "openHoursOne":"11:00-14:00",
      "openingHoursTwo":"15:00-17:00" 

  }

]

const App = ()  => {

  const [ showAdminPanel, setshowAdminPanel] = useState(false);
  const [ data, setData ] = useState(body);  

  const showAdminHandler = () => {
    setshowAdminPanel(showAdminPanel => !showAdminPanel); 
  } 

  let continents = [];
  data.forEach(d => continents.push(d.continent)); 
  let uniqueContinents = continents.filter((v, i, a) => a.indexOf(v) === i);


  return (
    <div className={styles.App}>
      <div className={styles.title}>
        <div>The Markets Open</div>
      </div>
      <div className={styles.mainContentBox}>

        {
          uniqueContinents.map( cont => 
            <RegionBox continentName={cont} data={data}/>
          )
        }



        <div className={styles.buttonContainer}>
          <button onClick={showAdminHandler}>Admin Panel</button>    
        </div>

          {showAdminPanel && <AdminPanel/>}

        <Footer/>

      </div>
    </div>
  );
}

export default App;
