import React, { useState, useEffect, useRef } from 'react'; 
import { Overlay, Popover } from 'react-bootstrap';

import './Clock.css'; 
import 'bootstrap/dist/css/bootstrap.min.css';






const Clock = ({ regionName, cityName, utcoffset, openHoursOne, openHoursTwo }) => {

    let city = cityName; 

    const initialState = { secondRatio: 0, minuteRatio: 0, hourRatio: 0 }; 

    //const [ cit, setCit ] = useState(''); 
    const [ clockState, setClockState ] = useState(initialState);
    const [ time, setTime ] = useState(0);  
    const [ dayTimeStr, setDayTimeStr ] = useState('');
    const [ openButton, showOpenButton ] = useState(true); 

    //for bs popup 
    const [show, setShow] = useState(false);
    const [target, setTarget] = useState(null);
    const ref = useRef(null);



    const handleClick = (event) => {
        setShow(!show);
        setTarget(event.target);
    };

    const digitalClockConverter = (date) => {


        //getting day of the week 
        const days = ['Sun','Mon','Tue','Wed','Thur','Fri','Sat'];
        let dayNum = Number(date.getDay()); 
        let stsDay = days[dayNum]; 


        //getting hours in am/pm 
        let hours = date.getHours();
        let minutes = date.getMinutes();
        let ampm = hours >= 12 ? 'PM' : 'AM';
        hours = hours % 12;
        hours = hours ? hours : 12; 
        minutes = minutes < 10 ? '0'+minutes : minutes;
        let strTime = hours + ':' + minutes + ' ' + ampm;

        let timeStr = stsDay + ' ' + strTime;

        setDayTimeStr( dayTimeStr => timeStr); 

    } 



    const clockStateHandler = (date) => {

        let secondRatio = date.getSeconds() / 60; 
        let minuteRatio = (secondRatio + date.getMinutes()) / 60; 
        let hourRatio = (minuteRatio + date.getHours()) / 12;  

        setClockState({...clockState, secondRate: secondRatio, minuteRate: minuteRatio, hourRate: hourRatio});

    }

    const clockHandler = () => {


        const date = new Date;

        let utc_offcet = date.getTimezoneOffset(); //offset is +8 from UTC
        date.setMinutes(date.getMinutes() + utc_offcet); 


        date.setMinutes(date.getMinutes() + utcoffset); 
        clockStateHandler(date); 
        digitalClockConverter(date);

        // switch(city){
        //     case 'Amsterdam':
        //         date.setMinutes(date.getMinutes() + utc2offcet); 
        //         clockStateHandler(date); 
        //         digitalClockConverter(date);
        //         return true; 
        //     case 'Brussels':
        //         date.setMinutes(date.getMinutes() + utc1offcet); 
        //         clockStateHandler(date); 
        //         digitalClockConverter(date); 
        //         return true;
        //     case 'London': 
        //         date.setMinutes(date.getMinutes() + utc1offcet); 
        //         clockStateHandler(date); 
        //         digitalClockConverter(date); 
        //         return true;
        //     case 'Paris': 
        //         date.setMinutes(date.getMinutes() + utc2offcet); 
        //         clockStateHandler(date); 
        //         digitalClockConverter(date); 
        //         return true;


        //     default:
        //         return 0; 
        // }

    }

    const calculateLocalTime = () => {
        //Amsterdam 19:00 is 01:00 locally => 6 hours 
        //Amsterdam utc +2 Local utc +8 => 6 hours difference 

        const date = new Date;
        const localUTC = Math.abs(date.getTimezoneOffset()); 

        let utcDiff = (localUTC/60) - (120 / 60); 
        //console.log(utcDiff); //6

        //11:00-14:00
        let countryHours = openHoursOne.split('-'); //['11:00', '14:00'] or instead ['11am', '2pm']
        let superResult = countryHours.forEach( num => num.split(':')); 


        let hoursFrom = Number(countryHours[0]);  
        //let hoursTo = countryHours[1].toNumber() + utcDiff; 
        console.log(superResult); 

    }

    calculateLocalTime(); 

    useEffect(() => {
        //console.log('CITYNAME', cityName); 
        const interval = setInterval(() => {
            clockHandler();
            setTime(Date.now())    
        }, 1000); 

        return () => {
            clearInterval(interval);
        };


    }, [time]); 


    return (
        <div className="mainContainer">
            <div className="top">
                <div className="mainFont">{cityName}</div>
                <div className="secondaryFont">{regionName.toUpperCase()}</div>
                <div className="mainFont">{dayTimeStr}</div>
            </div>

            <div class="middle">
                <div className="clock">
                    <div className="hand hour" 
                        style={{transform: `translate(-50%) rotate(${clockState.hourRate * 360}deg)`}}>
                    </div>
                    <div className="hand minute"
                        style={{transform: `translate(-50%) rotate(${clockState.minuteRate * 360}deg)`}}>
                    </div>
                    <div className="hand second"
                        style={{transform: `translate(-50%) rotate(${clockState.secondRate * 360}deg)`}}>
                    </div>

                    <div className="number number1">
                        <div>1</div>
                    </div>

                    <div className="number number2">
                        <div>2</div>
                    </div>

                    <div className="number number3">
                        <div>3</div>
                    </div>

                    <div className="number number4">
                        <div>4</div>
                    </div>

                    <div className="number number5">
                        <div>5</div>
                    </div>

                    <div className="number number6">
                        <div>6</div>
                    </div>

                    <div className="number number7">
                        <div>7</div>
                    </div>

                    <div className="number number8">
                        <div>8</div>
                    </div>

                    <div className="number number9">
                        <div>9</div>
                    </div>

                    <div className="number number10">
                        <div>10</div>
                    </div>

                    <div className="number number11">
                        <div>11</div>
                    </div>

                    <div className="number number12">
                        <div>12</div>
                    </div>
                </div>
            </div>

            <div className="bottom">
                <div>
                    {
                        openButton ? (<div className="oval green">Open</div>) : (<div className="oval red">Closed</div>)
                    }

                </div>
                <div className="addInfo">
                    <div>1:01:38 until break</div>
                    <div>5:01:38 until close</div>
                </div>

                <div ref={ref}>

                    <div className="oval2" onClick={handleClick}>Show Open/Close Hours</div>

                    <Overlay show={show} target={target} placement="top" containerPadding={20}>
                        <Popover id="popover-contained">
                            <Popover.Content>

                                <div class="contentBox">
                                    <div class="shedule">
                                        <div>{cityName.toUpperCase()} TIME</div>
                                        <div>{openHoursOne}</div>
                                    </div>
                                    <div class="shedule">
                                        <div>LOCAL TIME</div>
                                        <div>6:00AM - 1:00PM</div>
                                    </div>  
                                </div>

                            </Popover.Content>
                        </Popover>
                    </Overlay>
                </div>               
            </div>            
        </div>
    )
}

export default Clock;
