import React, { useState, useReducer } from 'react'; 
import { CountryDropdown, RegionDropdown, CountryRegionData } from 'react-country-region-selector';


const GeoSelector = () => {

    const initialGeoState = {  country: '', region: '' }; 

    const [ geoState, setGeoState ] = useState(initialGeoState); 

    const selectCountry = (val) => {
        setGeoState({...setGeoState, country: val}); 
    }

    const selectRegion = (val) => {
        setGeoState({...setGeoState, geoState: val}); 
    }


    return (
        <div>
            <CountryDropdown
                value={geoState.country}
                style={{ color: 'grey', fontSize: '16px'}}
                onChange={value => selectCountry(value)}

                >{geoState.country}</CountryDropdown>

            <RegionDropdown
                country={geoState.country}
                value={geoState.region}
                style={{ color: 'grey', fontSize: '16px'}}
                blankOptionLabel="No country selected."
                defaultOptionLabel="Now select a region."
                onChange={value => selectRegion(value)}
                >{geoState.state}</RegionDropdown>   
        </div>
    )
}

export default GeoSelector;
