import * as styles from './RegionBox.module.scss'; 
import { AiFillMinusCircle } from 'react-icons/ai'; 
import { AiFillPlusCircle } from 'react-icons/ai'; 

import Clock from '../clock/Clock'; 



const RegionBox = ({ continentName, data }) => {


    const [ show, setShow ] = useState(true);
    const [ isLoading, setIsLoading ] = useState(false); 

    console.log(data);

    const showHandler = () => {
        setShow(show => !show ); 
    }

    const continentMap = (returnArr, mapArr) => {

        returnArr = (<div className={styles.clockBoxes}>   
            { 
                mapArr.map(el => 

                    <Clock 
                    id={el['id']}
                    regionName={el['country']}
                    cityName={el['city']}
                    />) 
            }
        </div>); 

        return returnArr; 

    }

    //need to optimize this part 
    const renderByContinent = (continentName) =>{


        let europe = []; 
        let asia = []; 
        let southAmerica = []; 
        let northAmerica = []; 


        data.forEach(d => {

            if(d.continent === 'Europe'){
                europe.push(d); 
            } else if(d.continent === 'Asia'){
                asia.push(d);
            } else if(d.continent == 'South America'){
                southAmerica.push(d); 
            } else if(d.continent == 'South America'){
                northAmerica.push(d); 
            } 
        }); 

        //console.log(southAmerica); 

        //for europe 
        // let eur = (<div className={styles.clockBoxes}>   
        //     { 
        //         europe.map(el => 

