        //             regionName={el['country']}
        //             cityName={el['city']}
        //             />) 
        //     }
        // </div>); 

        //for asia 
        let as = (<div className={styles.clockBoxes}>   
            { 
                asia.map(el => 

                    <Clock 
                    id={el['id']}
                    regionName={el['country']}
                    cityName={el['city']}
                    />) 
            }
        </div>); 

        let southA = []; 
        continentMap(southA, southAmerica); 







        // (<div className={styles.clockBoxes}>   
        //     {/* <Clock 
        //         regionName={europe[0]}
        //         cityName={europe[0]}
        //     /> */}
        //     <Clock 

        //         regionName={'Belgium'}
        //         cityName={'Brussels'}
        //     />
        //     <Clock  
        //         regionName={'France'}
        //         cityName={'Paris'}
        //     />
        //     <Clock 
        //         regionName={'United Kingdom'}
        //         cityName={'London'}
        //     />
        // </div>); 

        // const southAmerica = 
        // (<div className={styles.clockBoxes}>   
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Argentina'}
        //         cityName={'Buenos Aires'}
        //     />
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Brazil'}
        //         cityName={'San Paulo'}
        //     />
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Chile'}
        //         cityName={'Santiago'}
        //     />

        // </div>); 

        // const northAmerica = 
        // (<div className={styles.clockBoxes}>   
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'USA'}
        //         cityName={'New York'}
        //     />
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Canada'}
        //         cityName={'Toronto'}
        //     />


        // </div>); 


        switch(continentName){
            case 'Asia': 
                return as; 
            case 'Europe': 
                return eur; 
            case 'South America': 
                return southA; 
            case 'North America':
                //return northA; 
            default: 
                return 0; 
        }
    }





    return (
        <div className={styles.regionBox}>
            <div className={styles.greyStripe}>
                <div className={styles.left}>{continentName}</div>
                <div className={styles.right}>
                    {isLoading && <div>Loading...</div>}
                    {show ?  
                        <AiFillMinusCircle onClick={showHandler}/> : 
                        <AiFillPlusCircle onClick={showHandler}/>
                    }  
                </div>
            </div>
            { show && renderByContinent(continentName)}

        </div>
    )
}

export default RegionBox;
