import React, { useState, useEffect } from 'react'; 
import * as styles from './RegionBox.module.scss'; 
import { AiFillMinusCircle } from 'react-icons/ai'; 
import { AiFillPlusCircle } from 'react-icons/ai'; 

import Clock from '../clock/Clock'; 



const RegionBox = ({ continentName }) => {

    const initialState = { secondRatio: 0, minuteRatio: 0, hourRatio: 0 }; 

    const [ clockState, setClockState ] = useState(initialState);
    const [ time, setTime ] = useState(0);  
    const [ show, setShow ] = useState(true); 

    const showHandler = () => {
        setShow(show => !show ); 
    }

    //need to optimize this part 
    const renderByContinent = (continentName) =>{
        const asia  = 
        (<div className={styles.clockBoxes}>   
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'China'}
                cityName={'Shanghai'}
            />
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'China SAR'}
                cityName={'Hong Kong'}
            />
        </div>); 

        const europe = 
        (<div className={styles.clockBoxes}>   
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'Netherlands'}
                cityName={'Amsterdam'}
            />
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'Belgium'}
                cityName={'Brussels'}
            />
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'France'}
                cityName={'Paris'}
            />
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'United Kingdom'}
                cityName={'London'}
            />
        </div>); 

        const southAmerica = 
        (<div className={styles.clockBoxes}>   
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'Argentina'}
                cityName={'Buenos Aires'}
            />
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'Brazil'}
                cityName={'San Paulo'}
            />
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'Chile'}
                cityName={'Santiago'}
            />

        </div>); 

        const northAmerica = 
        (<div className={styles.clockBoxes}>   
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'USA'}
                cityName={'New York'}
            />
            <Clock 
                secondRatio={clockState.secondRate}
                minuteRatio={clockState.minuteRate}
                hourRatio={clockState.hourRate}
                regionName={'Canada'}
                cityName={'Toronto'}
            />


        </div>); 


        switch(continentName){
            case 'Asia': 
                return asia; 
            case 'Europe': 
                return europe; 
            case 'South America': 
                return southAmerica; 
            case 'North America':
                return northAmerica; 
            default: 
                return 0; 
        }
    }


    const clockHandler = () => {
        const currentDate = new Date;

        let utc_offcet = currentDate.getTimezoneOffset(); //offset is +8 from UTC
        currentDate.setMinutes(currentDate.getMinutes() + utc_offcet); 



        //for Amsterdam UTC+2
        const amsterdamOffcet = 2*60; 
        currentDate.setMinutes(currentDate.getMinutes() + amsterdamOffcet); 

        let secondRatio = currentDate.getSeconds() / 60; 
        let minuteRatio = (secondRatio + currentDate.getMinutes()) / 60; 
        let hourRatio = (minuteRatio + currentDate.getHours()) / 12;  

        setClockState({...clockState, secondRate: secondRatio});
        setClockState({ ...clockState, minuteRate: minuteRatio });
        setClockState({ ...clockState, hourRate: hourRatio }); 

        //for Brussels UTC+1
        const BrusselOffcet = 60; 
        currentDate.setMinutes(currentDate.getMinutes() + BrusselOffcet); 

        secondRatio = currentDate.getSeconds() / 60; 
        minuteRatio = (secondRatio + currentDate.getMinutes()) / 60; 
        hourRatio = (minuteRatio + currentDate.getHours()) / 12;  

        setClockState({...clockState, secondRate: secondRatio});
        setClockState({ ...clockState, minuteRate: minuteRatio });
        setClockState({ ...clockState, hourRate: hourRatio }); 



    }

    useEffect(() => {

        const interval = setInterval(() => {
            clockHandler();
            setTime(Date.now())    
        }, 1000); 

        return () => {
            clearInterval(interval);
        };


    }, [time]); 


    return (
        <div className={styles.regionBox}>
            <div className={styles.greyStripe}>
                <div className={styles.left}>{continentName}</div>
                <div className={styles.right}>
                    {show ?  
                        <AiFillMinusCircle onClick={showHandler}/> : 
                        <AiFillPlusCircle onClick={showHandler}/>
                    }


                </div>
            </div>
            { show && renderByContinent(continentName)}

        </div>
    )
}

export default RegionBox;
