import React, {useState, useEffect} from 'react'; 
import './Clock.css'; 




const Clock = ({ regionName, cityName }) => {

    const initialState = { secondRatio: 0, minuteRatio: 0, hourRatio: 0 }; 

    const [ clockState, setClockState ] = useState(initialState);
    const [ time, setTime ] = useState(0);  


    const clockStateHandler = () => {
        let secondRatio = currentDate.getSeconds() / 60; 
        let minuteRatio = (secondRatio + currentDate.getMinutes()) / 60; 
        let hourRatio = (minuteRatio + currentDate.getHours()) / 12;  

        setClockState({...clockState, secondRate: secondRatio});
        setClockState({ ...clockState, minuteRate: minuteRatio });
        setClockState({ ...clockState, hourRate: hourRatio }); 
    }

    const clockHandler = (cityName) => {
        const date = new Date;

        let utc_offcet = date.getTimezoneOffset(); //offset is +8 from UTC
        date.setMinutes(date.getMinutes() + utc_offcet); 

        switch(cityName){
            case 'Amsterdam':
                const amsterdamOffcet = 2*60; 
                date.setMinutes(date.getMinutes() + amsterdamOffcet); 
            case 'Brussels': 
                const BrusselOffcet = 60; 
                date.setMinutes(date.getMinutes() + BrusselOffcet); 
        }



        //for Amsterdam UTC+2
        const amsterdamOffcet = 2*60; 
        currentDate.setMinutes(currentDate.getMinutes() + amsterdamOffcet); 



        //for Brussels UTC+1
        const BrusselOffcet = 60; 
        currentDate.setMinutes(currentDate.getMinutes() + BrusselOffcet); 

        // secondRatio = currentDate.getSeconds() / 60; 
        // minuteRatio = (secondRatio + currentDate.getMinutes()) / 60; 
        // hourRatio = (minuteRatio + currentDate.getHours()) / 12;  

        // setClockState({...clockState, secondRate: secondRatio});
        // setClockState({ ...clockState, minuteRate: minuteRatio });
        // setClockState({ ...clockState, hourRate: hourRatio }); 



    }

    useEffect(() => {

        const interval = setInterval(() => {
            clockHandler(cityName);
            setTime(Date.now())    
        }, 1000); 

        return () => {
            clearInterval(interval);
        };


    }, [time]); 


    return (
        <div class="mainContainer">
            <div class="top">
                <div class="mainFont">{cityName}</div>
                <div class="secondaryFont">{regionName.toUpperCase()}</div>
                <div class="mainFont">Monday 10:55AM</div>
            </div>

            <div class="middle">
                <div className="clock">
                    <div className="hand hour" 
                        style={{transform: `translate(-50%) rotate(${clockState.hourRatio * 360}deg)`}}>
                    </div>
                    <div className="hand minute"
                        style={{transform: `translate(-50%) rotate(${clockState.minuteRatio * 360}deg)`}}>
                    </div>
                    <div className="hand second"
                        style={{transform: `translate(-50%) rotate(${clockState.secondRatio * 360}deg)`}}>
                    </div>

                    <div className="number number1">
                        <div>1</div>
                    </div>

                    <div className="number number2">
                        <div>2</div>
                    </div>

                    <div className="number number3">
                        <div>3</div>
                    </div>

                    <div className="number number4">
                        <div>4</div>
                    </div>

                    <div className="number number5">
                        <div>5</div>
                    </div>

                    <div className="number number6">
                        <div>6</div>
                    </div>

                    <div className="number number7">
                        <div>7</div>
                    </div>

                    <div className="number number8">
                        <div>8</div>
                    </div>

                    <div className="number number9">
                        <div>9</div>
                    </div>

                    <div className="number number10">
                        <div>10</div>
                    </div>

                    <div className="number number11">
                        <div>11</div>
                    </div>

                    <div className="number number12">
                        <div>12</div>
                    </div>
                </div>
            </div>

            <div class="bottom">
                <div>
                    <div class="oval">Open</div>
                </div>
                <div class="addInfo">
                    <div>1:01:38 until break</div>
                    <div>5:01:38 until close</div>
                </div>
                <div class="oval2">View Open/Close times</div>
            </div>            
        </div>
    )
}

export default Clock;
