import React, { useState, useEffect } from 'react'; 
import * as styles from './RegionBox.module.scss'; 
import { AiFillMinusCircle } from 'react-icons/ai'; 
import { AiFillPlusCircle } from 'react-icons/ai'; 

import Clock from '../clock/Clock'; 

const body = 
[ 
    {
        "id":"123", 
        "continent":"Europe", 
        "country":"Netherlands",
        "city":"Amsterdam", 
        "utcoffset":"60",
        "openHoursOne":"11:00-14:00",
        "openingHoursTwo":"15:00-17:00" 

    }, 
    {
        "id":"456", 
        "continent":"Asia", 
        "country":"China",
        "city":"Shanghai", 
        "utcoffset":"480",
        "openHoursOne":"11:00-14:00",
        "openingHoursTwo":"15:00-17:00" 

    }, 
    {
        "id":"789", 
        "continent":"South America", 
        "country":"Brazil",
        "city":"San Paolo", 
        "utcoffset":"180",
        "openHoursOne":"11:00-14:00",
        "openingHoursTwo":"15:00-17:00" 

    }

]



const RegionBox = ({ continentName }) => {


    const [ show, setShow ] = useState(true);
    const [ isLoading, setIsLoading ] = useState(false); 
    const [ data, setData ] = useState(body);  

    const showHandler = () => {
        setShow(show => !show ); 
    }

    //need to optimize this part 
    const renderByContinent = (continentName) =>{
        // const asia  = 
        // (<div className={styles.clockBoxes}>   
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'China'}
        //         cityName={'Shanghai'}
        //     />
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'China SAR'}
        //         cityName={'Hong Kong'}
        //     />
        // </div>); 

        const europe = 
        (<div className={styles.clockBoxes}>   
            <Clock 
                regionName={'Netherlands'}
                cityName={'Amsterdam'}
            />
            <Clock 

                regionName={'Belgium'}
                cityName={'Brussels'}
            />
            <Clock  
                regionName={'France'}
                cityName={'Paris'}
            />
            <Clock 
                regionName={'United Kingdom'}
                cityName={'London'}
            />
        </div>); 

        // const southAmerica = 
        // (<div className={styles.clockBoxes}>   
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Argentina'}
        //         cityName={'Buenos Aires'}
        //     />
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Brazil'}
        //         cityName={'San Paulo'}
        //     />
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Chile'}
        //         cityName={'Santiago'}
        //     />

        // </div>); 

        // const northAmerica = 
        // (<div className={styles.clockBoxes}>   
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'USA'}
        //         cityName={'New York'}
        //     />
        //     <Clock 
        //         secondRatio={clockState.secondRate}
        //         minuteRatio={clockState.minuteRate}
        //         hourRatio={clockState.hourRate}
        //         regionName={'Canada'}
        //         cityName={'Toronto'}
        //     />


        // </div>); 


        switch(continentName){
            // case 'Asia': 
            //     return asia; 
            case 'Europe': 
                return europe; 
            // case 'South America': 
            //     return southAmerica; 
            // case 'North America':
            //     return northAmerica; 
            default: 
                return 0; 
        }
    }

    let allData = data.map( d => 
        <>
            <div key={d.id}>
                <div>{d.continent}</div>
                <div>{d.country}</div>
                <div>{d.city}</div>
                <div>{data.utcoffset}</div>
                <div>{data.openHoursOne}</div>
                <div>{data.openingHoursTwo}</div>
            </div>
        </>
    )



    return (
        <div className={styles.regionBox}>
            <div className={styles.greyStripe}>
                <div className={styles.left}>{continentName}</div>
                <div className={styles.right}>
                    {isLoading && <div>Loading...</div>}
                    {show ?  
                        <AiFillMinusCircle onClick={showHandler}/> : 
                        <AiFillPlusCircle onClick={showHandler}/>
                    }  

                    {allData}  
                </div>
            </div>
            { show && renderByContinent(continentName)}

        </div>
    )
}

export default RegionBox;
