const App = ()  => {

  let uniqueContinents = []; 

  const [ showAdminPanel, setshowAdminPanel] = useState(false);
  const [ data, setData ] = useState([]);  

  const showAdminHandler = () => {
    setshowAdminPanel(showAdminPanel => !showAdminPanel); 
  } 

  const url = 'https://rsqwsiljhg.execute-api.us-east-1.amazonaws.com/dev/clocksfunction'; 

  const fetchData = async(url) => {
    const response = await fetch(url);

    try{
      const body = await response.json(); 
      console.log('GOT THE BODY',body); 
      console.log(body.data.Items); 
      setData(data => body.data.Items);
    }catch(error){
      console.log('Error happened here!')
      console.error(error); 
    }

    //setLoading(true); 
  }

  const processData = () => {
      let continents = [];
      for (let key in data) {
        continents.push(data[key].continent);    
      }

    uniqueContinents = continents.filter((v, i, a) => a.indexOf(v) === i);
    console.log
  }



  //console.log('BEFORE FILTER', continents); 

  //console.log('AFTER FILTER', uniqueContinents); 


  useEffect(() => {
    fetchData(url); 
    processData(); 
  },[]); 


  return (
    <div className={styles.App}>
      <div className={styles.title}>
        <div>The Markets Open</div>
      </div>
      <div className={styles.mainContentBox}>

        {
          uniqueContinents.map( cont => 
            <RegionBox id={cont.id} continentName={cont} data={data}/>
          )
        }



        <div className={styles.buttonContainer}>
          <button onClick={showAdminHandler}>Admin Panel</button>    
        </div>

          {showAdminPanel && <AdminPanel/>}

        <Footer/>

      </div>
    </div>
  );
}

export default App;
