import React, { useState } from 'react'; 
import Form from 'react-bootstrap/Form';

import * as styles from './ContinentDropdown.module.scss'; 


const ContinentDropdown = () => {

    const [ selectedContinent, setSelectedContinent ] = useState(''); 

    console.log(selectedContinent); 


    return (
        <div className={styles.ddWrapper}>
            <Form>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Select Continent</Form.Label>
                    <Form.Control as="select">
                        <option value={selectedContinent} onChange={e => setSelectedContinent(e.target.value)}>Select continent</option>
                        <option >Asia</option>
                        <option>Asia</option>
                        <option>Europe</option>
                        <option>North America</option>
                        <option>South America</option>
                    </Form.Control>
                </Form.Group>
            </Form>
        </div>       
    )
}

export default ContinentDropdown;
