            case 'Europe': 
                return europe; 
            // case 'South America': 
            //     return southAmerica; 
            // case 'North America':
            //     return northAmerica; 
            default: 
                return 0; 
        }
    }

    let allData = data.map( d => 
        <>
            <div key={d.id}>
                <div>{d.continent}</div>
                <div>{d.country}</div>
                <div>{d.city}</div>
                <div>{d.utcoffset}</div>
                <div>{d.openHoursOne}</div>
                <div>{d.openingHoursTwo}</div>
            </div>
        </>
    )



    return (
        <div className={styles.regionBox}>
            <div className={styles.greyStripe}>
                <div className={styles.left}>{continentName}</div>
                <div className={styles.right}>
                    {isLoading && <div>Loading...</div>}
                    {show ?  
                        <AiFillMinusCircle onClick={showHandler}/> : 
                        <AiFillPlusCircle onClick={showHandler}/>
                    }  
                </div>
            </div>
            { show && renderByContinent(continentName)}

        </div>
    )
}

export default RegionBox;
